一.线段树（无优化）1.维护区间最大值(最小值)#include&lt;bits/stdc++.h&gt;#define int long long#define mid (l+r)/2#define lson l,mid,rt&lt;&lt;1#define rson mid+1,r,rt&lt;&lt;1|1using namespace std;int n,m,tag[2000005],mx[2000005];void pushup(int rt)&#123;	mx[rt]=max(mx[rt&lt;&lt;1],mx[rt&lt;&lt;1|1]);&#125;void pushdown(int l,int r,int rt)&#123;	int res=r-l+1;	tag[rt&lt;&lt;1]+=tag[rt];	tag[rt&lt;&lt;1|1]+=tag[rt];	mx[rt&lt;&lt;1]+=tag[rt];	mx[rt&lt;&lt;1|1]+=tag[rt];	tag[rt]=0;&#125;void build(int l,int r,int rt)&#123;	if(l==r)	&#123;		cin&gt;&gt;mx[rt];		return;	&#125;	build(lson);	build(rson);	pushup(rt);&#125;int query(int a,int b,int l,int r,int rt)&#123;	if(a&lt;=l&amp;&amp;r&lt;=b)return mx[rt];	int ans=-1;	if(tag[rt])pushdown(l,r,rt);	if(a&lt;=mid)ans=max(ans,query(a,b,lson));	if(b&gt;mid)ans=max(ans,query(a,b,rson));	return ans;&#125;void mupdate(int l,int r,int rt,int a,int b,int num)&#123;	if(a&lt;=l&amp;&amp;r&lt;=b)	&#123;		mx[rt]=max(num,mx[rt]);        tag[rt]+=num;		return;	&#125;	if(tag[rt])pushdown(l,r,rt);	if(a&lt;=mid)mupdate(lson,a,b,num);	if(b&gt;mid)mupdate(rson,a,b,num);	pushup(rt);&#125;signed main()&#123;	cin&gt;&gt;n&gt;&gt;m;	build(1,n,1);	for(int i=1;i&lt;=m;i++)	&#123;			&#125;&#125;

2.维护区间和#include&lt;bits/stdc++.h&gt;#define int long long#define mid (l+r)/2#define lson l,mid,rt&lt;&lt;1#define rson mid+1,r,rt&lt;&lt;1|1using namespace std;int n,m,sum[2000005],tag[2000005];void pushup(int rt)&#123;	sum[rt]=sum[rt&lt;&lt;1]+sum[rt&lt;&lt;1|1];&#125;void pushdown(int l,int r,int rt)&#123;	int res=r-l+1;	tag[rt&lt;&lt;1]+=tag[rt];	tag[rt&lt;&lt;1|1]+=tag[rt];	sum[rt&lt;&lt;1]+=tag[rt]*(res-(res&gt;&gt;1));	sum[rt&lt;&lt;1|1]+=tag[rt]*(res&gt;&gt;1);	tag[rt]=0;&#125;void build(int l,int r,int rt)&#123;	if(l==r)	&#123;		cin&gt;&gt;sum[rt];		return;	&#125;	build(lson);	build(rson);	pushup(rt);&#125;int query(int a,int b,int l,int r,int rt)&#123;	if(a&lt;=l&amp;&amp;r&lt;=b)return sum[rt];	int ans=0;	if(tag[rt])pushdown(l,r,rt);	if(a&lt;=mid)ans+=query(a,b,lson);	if(b&gt;mid)ans+=query(a,b,rson);	return ans;&#125;void mupdate(int l,int r,int rt,int a,int b,int num)&#123;	if(a&lt;=l&amp;&amp;r&lt;=b)	&#123;		sum[rt]+=(r-l+1)*num;		tag[rt]+=num;		return;	&#125;	if(tag[rt])pushdown(l,r,rt);	if(a&lt;=mid)mupdate(lson,a,b,num);	if(b&gt;mid)mupdate(rson,a,b,num);	pushup(rt);&#125;signed main()&#123;	cin&gt;&gt;n&gt;&gt;m;	build(1,n,1);	for(int i=1;i&lt;=m;i++)	&#123;			&#125;&#125;
二.快速读写1.快读inline int read()&#123;    int x=0,f=1;	char ch=getchar();    while(!isdigit(ch))&#123;if (ch==&#x27;-&#x27;)f=-1;ch=getchar();&#125;    while(isdigit(ch))&#123;x=x*10+ch-48;ch=getchar();&#125;    return x*f;&#125;
2.快输inline int write(int x)&#123;    if(x&lt;0)&#123;x=~(x-1);putchar(&#x27;-&#x27;);&#125;    if(x&gt;9)write(x/10);    putchar(x%10+&#x27;0&#x27;);&#125;
三.最短路1.迪杰斯特拉#include&lt;bits/stdc++.h&gt;#define pi pair&lt;int,int&gt;#define inf 2147483647using namespace std;int head[100005],s,cnt=0,n,m,vis[100005],dis[100005];struct edge&#123;	int u,v,w,nxt;&#125;e[200005];priority_queue &lt;pi,vector&lt;pi&gt;,greater&lt;pi&gt; &gt;q;void adde(int a,int b,int c)&#123;	e[++cnt].u=a;	e[cnt].v=b;	e[cnt].w=c;	e[cnt].nxt=head[a];	head[a]=cnt;&#125;void dijkstra()&#123;	while(!q.empty())	&#123;		int u=q.top().second;		q.pop();		if(vis[u])continue;		vis[u]=1;		for(int i=head[u];i;i=e[i].nxt)		&#123;			int v=e[i].v;			if(dis[v]&gt;dis[u]+e[i].w)			&#123;				dis[v]=dis[u]+e[i].w;				if(vis[v]==0)q.push(make_pair(dis[v],v)); 			&#125;		&#125;	&#125;&#125;int main()&#123;	scanf(&quot;%d%d%d&quot;,&amp;n,&amp;m,&amp;s);	for(int i=1;i&lt;=m;i++)	&#123;		long long a,b,c;		scanf(&quot;%lld%lld%lld&quot;,&amp;a,&amp;b,&amp;c);		adde(a,b,c);	&#125;	for(int i=1;i&lt;=n;i++)dis[i]=inf; 	dis[s]=0;	q.push(make_pair(0,s));	dijkstra();	for(int i=1;i&lt;=n;i++)printf(&quot;%d &quot;,dis[i]);&#125;
四.质数1.素数筛（欧拉筛）#include&lt;bits/stdc++.h&gt;#define int long longusing namespace std;int n,p[10000005],cnt=0;bool np[10000005];void eusi(int x)&#123;	np[0]=np[1]=1;	for(int i=2;i&lt;=x;i++)	&#123;		if(!np[i])p[++cnt]=i;		for(int j=1;j&lt;=cnt;j++)		&#123;			if(i*p[j]&gt;x)break;			np[i*p[j]]=1;			if(i%p[j]==0)break;		&#125;	&#125;&#125;signed main()&#123;	cin&gt;&gt;n;	eusi(n);	for(int i=1;i&lt;=cnt;i++)cout&lt;&lt;p[i]&lt;&lt;&quot; &quot;;&#125;
